    <script>
        $(document).ready(function() {
            // When brand changes  load models
            $('#brand-select').on('change', function() {
                var brandId = $(this).val();
                $('#model-select').html('<option value="">Loading...</option>');

                if (brandId) {
                    $.get("{{ url('/get-models') }}/" + brandId, function(data) {
                        let options = '<option value="">Select Model</option>';
                        data.forEach(model => {
                            options +=
                                `<option value="${model.id}">${model.title}</option>`;
                        });
                        $('#model-select').html(options);
                    });
                } else {
                    $('#model-select').html('<option value="">Select Model</option>');
                }
            });

            // Tabs for All / New / Used  map to hidden condition field
            $('#condition-tabs').on('click', '.item-title', function(e) {
                e.preventDefault();
                $('#condition-tabs .item-title').removeClass('active');
                $(this).addClass('active');

                const condition = $(this).data('condition') || '';
                $('#condition-input').val(condition);
            });

        });
    </script>
